	<script>
		const data = JSON.parse('[{"x":"(1) Penurunan MHS","y":12.83,"indikator":"Rerata persentase penurunan mahasiswa baru (S1, D4, D3) dalam 5 tahun terakhir","kategori":"Persen","satuan":"PPM","jenis":"lebih kecil dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":20,"strokeWidth":5,"strokeColor":"#FF90BC"}]},{"x":"(2) PS dgn Dosen Homebase","y":70,"indikator":"Semua program studi aktif memiliki Dosen Homebase (NIDN\/NIDK)","kategori":"Persen","satuan":"(DH \u2265 5)","jenis":"sama dengan","color":"#F39C12","goals":[{"name":"SN-DIKTI","value":100,"strokeWidth":5,"strokeColor":"#FF90BC"}]},{"x":"(3) Keterlibatan DTT","y":0,"indikator":"Keterlibatan dosen tidak tetap","kategori":"Persen","satuan":"DTT","jenis":"lebih kecil dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":40,"strokeWidth":5,"strokeColor":"#FF90BC"}]},{"x":"(4) Mahasiswa Aktif per DT","y":28.59,"indikator":"Jumlah mahasiswa aktif (S1, D4, D3) dibagi jumlah dosen tetap saat TS","kategori":"Rasio","satuan":"RM\/DT ","jenis":"lebih kecil dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":40,"strokeWidth":5,"strokeColor":"#FF90BC"}]},{"x":"(5) Penurunan Lulusan 5th","y":8.61,"indikator":"Rerata persentase penurunan lulusan (S1, D4, D3) dalam 5 tahun terakhir","kategori":"Persen","satuan":"RPL","jenis":"lebih kecil dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":20,"strokeWidth":5,"strokeColor":"#FF90BC"}]},{"x":"(6) PS Aktif Terakreditasi","y":60,"indikator":"Semua PS aktif terakreditasi","kategori":"Persen","satuan":"PSA","jenis":"sama dengan","color":"#F39C12","goals":[{"name":"SN-DIKTI","value":100,"strokeWidth":5,"strokeColor":"#FF90BC"}]},{"x":"(7) Guru Besar PS Doktor","y":0,"indikator":"Jumlah guru besar sebagai dosen homebase sekurangnya 2 orang per program doktor","kategori":"Jumlah","satuan":"DGB","jenis":"lebih besar dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":2,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(8) DT GB+LK+L+AA","y":47.14,"indikator":"Persentase DT memiliki jabatan akademik (GB+LK+L+AA)","kategori":"Persen","satuan":"DTJA","jenis":"lebih besar dari atau sama dengan","color":"#FF0000","goals":[{"name":"SN-DIKTI","value":60,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(9) PKMTK S1 D4 D3","y":31,"indikator":"Kelulusan tepat masa tempuh kurikulum (mahasiswa S1\/D4 masuk TS-3 lulus sampai TS, mahasiswa D3 masuk TS-2 lulus TS dan rerata pengamatan dalam tiga tahun terakhir untuk tiga angkatan mahasiswa terakhir)","kategori":"Persen","satuan":"PKMTK","jenis":"lebih besar dari atau sama dengan","color":"#F39C12","goals":[{"name":"SN-DIKTI","value":35,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(10) PK2MTK S1 D4 D3","y":52,"indikator":"Kelulusan tepat 2x waktu tempuh kurikulum (mahasiswa S1\/D4 masuk TS-6 lulus sampai TS, mahasiswa D3 masuk TS-4 lulus sampai TS dan rerata pengamatan dalam tiga tahun terakhir untuk tiga angkatan mahasiswa terakhir)","kategori":"Persen","satuan":"PK2MTK","jenis":"lebih besar dari atau sama dengan","color":"#F39C12","goals":[{"name":"SN-DIKTI","value":60,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(11) Keterlibatan MHS Prestasi","y":0,"indikator":"Persentase keterlibatan mahasiswa aktif dalam memperoleh prestasi mahasiswa tingkat internasional nasional\/provinsi peringkat 1, 2 dan 3 saat TS","kategori":"Persen","satuan":"PMAP","jenis":"lebih besar dari atau sama dengan","color":"#F39C12","goals":[{"name":"SN-DIKTI","value":0.01,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(12) Terserap Lapangan Kerja","y":32,"indikator":"Persentase lulusan terserap lapangan kerja kurang dari sama dengan 1 tahun saat tahun lulusan TS-2","kategori":"Persen","satuan":"PLLK","jenis":"lebih besar dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":20,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(13) Luaran Penelitian","y":21.43,"indikator":"Rerata persentase luaran penelitian dan PkM dalam bentuk Jurnal DT terindeks (Scopus & Sinta 2) dalam 3 tahun saat TS","kategori":"Persen","satuan":"PPPKM","jenis":"lebih besar dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":10,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(14) Mahasiswa MBKM","y":0,"indikator":"Kepesertaan mahasiswa yang eligible yang mengikuti MBKM saat TS","kategori":"Persen","satuan":"MEMBKM","jenis":"lebih besar dari atau sama dengan","color":"#F39C12","goals":[{"name":"SN-DIKTI","value":10,"strokeWidth":5,"strokeColor":"#8E44AD"}]},{"x":"(15) Karya DT","y":14,"indikator":"Karya DT yang terekognisi\/diterapkan masyarakat","kategori":"Jumlah","satuan":"PKDT","jenis":"lebih besar dari atau sama dengan","color":"#27AE60","goals":[{"name":"SN-DIKTI","value":10,"strokeWidth":5,"strokeColor":"#8E44AD"}]}]');

		document.addEventListener("DOMContentLoaded", function() {
			var stroke_target_color = '#8E44AD';
			var ser_data = [{
				name: 'Aktual',
				data: data
			}];
			var options = {
				series: ser_data,
				chart: {
					height: (data.length * 50),
					type: 'bar',
					toolbar: {
						show: false
					}
				},
				plotOptions: {
					bar: {
						horizontal: true,
						dataLabels: {
							position: 'bottom'
						},
					}
				},
				yaxis: {
					min: 0,
					max: 100
				},
				colors: [
					function({
						value,
						seriesIndex,
						dataPointIndex,
						w
					}) {
						if (ser_data[seriesIndex].data[dataPointIndex].color === null) {
							const target_val = ser_data[seriesIndex].data[dataPointIndex].goals[0].value;
							var target_percent = value != 0 && target_val != 0 ? (value / target_val) : 0;

							if (ser_data[seriesIndex].data[dataPointIndex].jenis === 'lebih besar dari atau sama dengan') {
								if (target_percent < 0.5) {
									return '#FF0000';
								} else if (target_percent >= 0.5 && target_percent < 1.0) {
									return '#F39C12';
								} else if (target_percent >= 1.0) {
									return '#27AE60';
								}
							} else {
								if (target_percent >= 1.0 && target_percent < 1.5) {
									return '#F39C12';
								} else if (target_percent <= 1.0) {
									return '#27AE60';
								} else {
									return '#FF0000';
								}
							}
						} else {
							return ser_data[seriesIndex].data[dataPointIndex].color;
						}
					}
				],
				dataLabels: {
					enabled: true,
					textAnchor: 'start',
					style: {
						colors: ['#000']
					},
					formatter: function(val, opt) {
						const goals =
							opt.w.config.series[opt.seriesIndex].data[opt.dataPointIndex]
							.goals;
						const data = opt.w.config.series[opt.seriesIndex].data[opt.dataPointIndex];

						if (goals && goals.length) {
							return `${val}${data.kategori === 'Persen' || data.kategori === 'Rasio' ? '%' : ` ${data.satuan}`} / ${goals[0].value}${data.kategori === 'Persen' || data.kategori === 'Rasio' ? '%' : ` ${data.satuan}`}`
						}
						return val
					}
				},
				tooltip: {
					followCursor: true,
					custom: function({
						series,
						seriesIndex,
						dataPointIndex,
						w
					}) {
						var data = w.globals.initialSeries[seriesIndex].data[dataPointIndex];
						return `
                    <p class="mb-0">${data.indikator}</p>
                    <ul class="mb-1">
                        <li>Aktual: ${data.kategori === 'Rasio' ? '1:' : ''}${data.y}${data.kategori === 'Persen' ? '%' : ` ${data.satuan}`}</li>
                        <li>Ambang Batas: ${data.kategori === 'Rasio' ? '1:' : ''}${data.goals[0].value}${data.kategori === 'Persen' ? '%' : ` ${data.satuan}`}</li>
                    </ul>
                    `;
					}
				},
				legend: {
					show: true,
					showForSingleSeries: true,
					customLegendItems: ['Aktual', 'BAN PT'],
					markers: {
						fillColors: ['#00E396', '#775DD0']
					}
				},
			};

			if ($('#capaian-bar').length > 0) {
				var chart = new ApexCharts(document.querySelector("#capaian-bar"), options);
				chart.render();
			}
		});

		function generate() {
			Swal.fire({
				title: "Apa anda yakin?",
				text: "Skor terhadap afiliasi ini akan diperbarui apabila anda menekan tombol Ya",
				icon: "warning",
				showCancelButton: true,
				confirmButtonColor: "#3085d6",
				cancelButtonColor: "#d33",
				cancelButtonText: "Tidak!",
				confirmButtonText: "Ya, perbarui!"
			}).then((result) => {
				if (result.isConfirmed) {
					$('#loading_screen').addClass('d-flex').removeClass('d-none');
					$.ajax({
						method: 'POST',
						url: "https://pemutu.kemdikbud.go.id/sync/afiliasi/041046/1/3",
						dataType: 'json',
						success: function(res) {
							if (res.success === true) {
								Swal.fire('Berhasil', 'Skor terhadap Universitas Informatika Dan Bisnis Indonesia telah berhasil diubah. Anda diminta untuk merefresh halaman ini untuk memperbarui skornya.', 'success').then(() => window.location.reload());
							} else {
								Swal.fire('Gagal', res.message, 'success');
							}
						},
						complete: function() {
							$('#loading_screen').removeClass('d-flex').addClass('d-none');
						}
					})
				}
			});
		}
	</script>
